#!/usr/bin/env ruby

string = ""

def multiply(string)
  string.split(//).inject(1) {|product,n| product * n.to_i}
end

start = 0
length = 13
stop = false
match = "0420752963450"
result = Hash.new

while stop == false
  new_string = string.slice(start,length)
  result[new_string] = multiply(new_string)
  puts result
  if new_string == match
    stop = true
  else
    start += 1
  end
end

puts result.sort_by{|k,v| v}